    if (!req.files) {
      return res.status(500).send({
        msg: "file is not found"
      });
    } // accessing the file


    var myFile = req.files.file; //  mv() method places the file inside public directory

    myFile.mv("".concat(__dirname, "/public/").concat(myFile.name), function (err) {
      if (err) {
        console.log(err);
        return res.status(500).send({
          msg: "Error occured"
        });
      }

      processLineByLine("".concat(__dirname, "/public/").concat(myFile.name), res, req); // returing the response with file path and name

      return res.send({
        name: myFile.name,
        path: "/public/".concat(myFile.name)
      });
    });
  } catch (error) {
    res.status(500).json({
      message: "Error uploading file",
      error: error
    });
  }
});

