const createPdf = async (invoice, fileName) => {
  const doc = new PDFDocument();
  console.log(fileName);
  doc.pipe(fs.createWriteStream(fileName));
  const startPoint = 50;
  doc.fontSize(25).text(`Invoice # ${invoice.order.orderId}`, startPoint, 100);
  doc
    .fontSize(25)
    .text("Invoice Date: " + invoice.company.invoiceDate, startPoint, 130);

  doc
    .fontSize(25)
    .text(
      "----------------------------------------------------------",
      startPoint,
      160
    );

  doc.fontSize(12).text("Customer: " + invoice.order.customer, startPoint, 190);
  doc.fontSize(12).text("Order Id: " + invoice.order.orderId, startPoint, 210);
  doc
    .fontSize(12)
    .text("Order date: " + invoice.order.orderDate, startPoint, 230);
  doc
    .fontSize(25)
    .text(
      "----------------------------------------------------------",
      startPoint,
      250
    );
  doc.fontSize(12).text("Sold by: " + invoice.company.Company, startPoint, 280);
  doc
    .fontSize(25)
    .text(
      "----------------------------------------------------------",
      startPoint,
      300
    );

  let row = 330;
  let index = 1;
  invoice.order.products.map((item) => {
    doc
      .fontSize(12)
      .text(
        index +
          ". " +
          item.name +
          " - Qty: " +
          item.quantity +
          " - Unit Price: $" +
          item.price,
        startPoint,
        row
      );
    row += 20;
    index += 1;
  });
  doc
    .fontSize(12)
    .text("Total: $" + invoice.order.total, startPoint + 401, row);
  doc
    .fontSize(25)
    .text(
      "----------------------------------------------------------",
      startPoint,
      (row += 20)
    );
  doc
    .image(
      `./${invoice.company.Company}-${invoice.order.orderId}.png`,
      420,
      30,
      {
        fit: [100, 100],
      }
    )
    .rect(420, 30, 100, 100)
    .stroke()
    .text("QR", 420, 0);
  doc.end();
};

app.get("/createPdf/", async (req, res) => {
  try {
    const invoice = JSON.parse(req.query.data);
    const fileName = `${__dirname}/public/${invoice.company.Company}-${invoice.order.orderId}.pdf`;
    if (fs.existsSync(fileName)) {
      res.download(fileName);
    } else {
      let stringInvoice = JSON.stringify(invoice.order.products);
      await QRCode.toFile(
        `./${invoice.company.Company}-${invoice.order.orderId}.png`,
        stringInvoice
      );
      await createPdf(invoice, fileName);
      await sleep(300);
      fs.unlink(
        `./${invoice.company.Company}-${invoice.order.orderId}.png`,
        (err) => {
          if (err) {
            console.error(err);
            return;
          }
          console.log("png file removed");
        }
      );
      res.download(fileName);
    }
  } catch (error) {
    res.status(500).json({ message: "Error creating invoice", error });
  }
});

