app.get("/popularProds", async (req, res) => {
  withDB(
    async (db) => {
      const orders = await db
        .collection("orders")
        .aggregate([
          { $unwind: "$data.products" },
          {
            $group: {
              _id: {
                barcode: "$data.products.barcode",
                name: "$data.products.name",
              },
              count: { $sum: 1 },
            },
          },
          {
            $group: {
              _id: "$_id.barcode",
              values: { $push: { name: "$_id.name", count: "$count" } },
            },
          },
          { $sort: { _id: -1 } },
        ])
        .toArray();
      res.status(200).json(orders);
    },
    res,
    req.query[0]
  );
});

