        .toArray();
      let responseServer = 0;
      if (lastInvoice.length > 0) {
        responseServer = parseInt(lastInvoice[0].data.invoiceId) + 1;
      } else {
        responseServer = 5000;
      }
      res.status(200).json(responseServer);
    },
